package com.paxel.arspacescan.data.local

import android.content.Context
import androidx.room.*
import com.paxel.arspacescan.data.model.PackageMeasurement

@Database(
    entities = [PackageMeasurement::class],
    version = 1,
    exportSchema = false
)
@TypeConverters(Converters::class)
abstract class AppDatabase : RoomDatabase() {

    abstract fun measurementDao(): MeasurementDao

    companion object {
        @Volatile
        private var INSTANCE: AppDatabase? = null

        fun getDatabase(context: Context): AppDatabase {
            return INSTANCE ?: synchronized(this) {
                val instance = Room.databaseBuilder(
                    context.applicationContext,
                    AppDatabase::class.java,
                    "paxel_measurement_database"
                ).build()
                INSTANCE = instance
                instance
            }
        }
    }
}

package com.paxel.arspacescan.data.local

import android.content.Context
import androidx.room.*
import com.paxel.arspacescan.data.model.PackageMeasurement

@Database(
    entities = [PackageMeasurement::class],
    version = 2,
    exportSchema = false
)
@TypeConverters(Converters::class)
abstract class AppDatabase : RoomDatabase() {

    abstract fun measurementDao(): MeasurementDao

    companion object {
        @Volatile
        private var INSTANCE: AppDatabase? = null

        fun getDatabase(context: Context): AppDatabase {
            return INSTANCE ?: synchronized(this) {
                val instance = Room.databaseBuilder(
                    context.applicationContext,
                    AppDatabase::class.java,
                    "paxel_measurement_database"
                )
                .fallbackToDestructiveMigration()
                .build()
                INSTANCE = instance
                instance
            }
        }
    }
}

package com.paxel.arspacescan.ui.common

import android.os.Bundle
import android.view.LayoutInflater
import android.view.View
import android.view.ViewGroup
import android.widget.ArrayAdapter
import com.google.android.material.bottomsheet.BottomSheetDialogFragment
import com.google.android.material.textfield.MaterialAutoCompleteTextView
import com.google.android.material.textfield.TextInputEditText
import com.paxel.arspacescan.R
import com.paxel.arspacescan.databinding.BottomSheetPackageDetailsBinding

class PackageDetailBottomSheet : BottomSheetDialogFragment() {

    private var _binding: BottomSheetPackageDetailsBinding? = null
    private val binding get() = _binding!!

    private var listener: OnPackageDetailsListener? = null

    interface OnPackageDetailsListener {
        fun onPackageDetailsSubmitted(packageName: String, declaredSize: String)
    }

    override fun onCreateView(
        inflater: LayoutInflater,
        container: ViewGroup?,
        savedInstanceState: Bundle?
    ): View {
        _binding = BottomSheetPackageDetailsBinding.inflate(inflater, container, false)
        return binding.root
    }

    override fun onViewCreated(view: View, savedInstanceState: Bundle?) {
        super.onViewCreated(view, savedInstanceState)

        setupDeclaredSizeDropdown()
        setupButtons()
    }

    private fun setupDeclaredSizeDropdown() {
        val declaredSizes = arrayOf(
            "Kecil (hingga 1000 cm³)",
            "Sedang (1001 - 5000 cm³)",
            "Besar (lebih dari 5000 cm³)"
        )

        val adapter = ArrayAdapter(
            requireContext(),
            android.R.layout.simple_dropdown_item_1line,
            declaredSizes
        )

        binding.autoCompleteTextView.setAdapter(adapter)
        binding.autoCompleteTextView.setText(declaredSizes[1], false) // Default to medium
    }

    private fun setupButtons() {
        binding.btnCancel.setOnClickListener {
            dismiss()
        }

        binding.btnSubmit.setOnClickListener {
            val packageName = binding.etPackageName.text?.toString()?.trim() ?: ""
            val declaredSize = binding.autoCompleteTextView.text?.toString() ?: ""

            if (packageName.isEmpty()) {
                binding.tilPackageName.error = "Nama paket tidak boleh kosong"
                return@setOnClickListener
            }

            listener?.onPackageDetailsSubmitted(packageName, declaredSize)
            dismiss()
        }
    }

    fun setOnPackageDetailsListener(listener: OnPackageDetailsListener) {
        this.listener = listener
    }

    override fun onDestroyView() {
        super.onDestroyView()
        _binding = null
    }

    companion object {
        const val TAG = "PackageDetailBottomSheet"

        fun newInstance(): PackageDetailBottomSheet {
            return PackageDetailBottomSheet()
        }
    }
}

package com.paxel.arspacescan.ui.common

import android.os.Build
import android.view.HapticFeedbackConstants
import android.view.View

/**
 * Safely performs haptic feedback on a view with error handling
 */
fun View.safeHapticFeedback(feedbackConstant: Int = HapticFeedbackConstants.VIRTUAL_KEY) {
    try {
        if (Build.VERSION.SDK_INT >= Build.VERSION_CODES.O) {
            performHapticFeedback(feedbackConstant, HapticFeedbackConstants.FLAG_IGNORE_GLOBAL_SETTING)
        } else {
            performHapticFeedback(feedbackConstant)
        }
    } catch (e: Exception) {
        // Silently ignore haptic feedback errors
        // Some devices or configurations may not support haptic feedback
    }
}

/**
 * Extension function for safe click haptic feedback
 */
fun View.safeClickHapticFeedback() {
    try {
        if (Build.VERSION.SDK_INT >= Build.VERSION_CODES.R) {
            performHapticFeedback(HapticFeedbackConstants.CONFIRM)
        } else {
            performHapticFeedback(HapticFeedbackConstants.VIRTUAL_KEY)
        }
    } catch (e: Exception) {
        // Silently ignore haptic feedback errors
    }
}

package com.paxel.arspacescan.ui.common

import android.os.Build
import android.view.HapticFeedbackConstants
import android.view.View

/**
 * Safely performs haptic feedback on a view with error handling
 */
fun View.safeHapticFeedback(feedbackConstant: Int = HapticFeedbackConstants.VIRTUAL_KEY) {
    try {
        if (Build.VERSION.SDK_INT >= Build.VERSION_CODES.R) {
            // Use modern haptic feedback without deprecated flags
            performHapticFeedback(feedbackConstant)
        } else if (Build.VERSION.SDK_INT >= Build.VERSION_CODES.O) {
            // For older versions, use alternative approach
            performHapticFeedback(feedbackConstant)
        } else {
            performHapticFeedback(feedbackConstant)
        }
    } catch (e: Exception) {
        // Silently ignore haptic feedback errors
        // Some devices or configurations may not support haptic feedback
    }
}

/**
 * Extension function for safe click haptic feedback
 */
fun View.safeClickHapticFeedback() {
    try {
        if (Build.VERSION.SDK_INT >= Build.VERSION_CODES.R) {
            // Use CONFIRM for modern devices
            performHapticFeedback(HapticFeedbackConstants.CONFIRM)
        } else if (Build.VERSION.SDK_INT >= Build.VERSION_CODES.O) {
            // Use VIRTUAL_KEY for older devices
            performHapticFeedback(HapticFeedbackConstants.VIRTUAL_KEY)
        } else {
            // Fallback for very old devices
            performHapticFeedback(HapticFeedbackConstants.VIRTUAL_KEY)
        }
    } catch (e: Exception) {
        // Silently ignore haptic feedback errors
    }
}

package com.paxel.arspacescan.ui.history

import android.view.LayoutInflater
import android.view.View
import android.view.ViewGroup
import android.widget.ImageView
import android.widget.TextView
import androidx.recyclerview.widget.DiffUtil
import androidx.recyclerview.widget.ListAdapter
import androidx.recyclerview.widget.RecyclerView
import com.bumptech.glide.Glide
import com.bumptech.glide.load.resource.bitmap.CenterCrop
import com.bumptech.glide.load.resource.bitmap.RoundedCorners
import com.google.android.material.button.MaterialButton
import com.paxel.arspacescan.R
import com.paxel.arspacescan.data.model.MeasurementResult
import java.text.SimpleDateFormat
import java.util.Date
import java.util.Locale

class MeasurementAdapter(
    private val onItemClick: (MeasurementResult) -> Unit,
    private val onDeleteClick: (MeasurementResult) -> Unit
) : ListAdapter<MeasurementResult, MeasurementAdapter.ViewHolder>(MeasurementDiffCallback()) {

    override fun onCreateViewHolder(parent: ViewGroup, viewType: Int): ViewHolder {
        val view = LayoutInflater.from(parent.context)
            .inflate(R.layout.item_measurement, parent, false)
        return ViewHolder(view)
    }

    override fun onBindViewHolder(holder: ViewHolder, position: Int) {
        holder.bind(getItem(position))
    }

    inner class ViewHolder(itemView: View) : RecyclerView.ViewHolder(itemView) {
        private val ivPackageThumbnail: ImageView = itemView.findViewById(R.id.ivPackageThumbnail)
        private val tvPackageName: TextView = itemView.findViewById(R.id.tvPackageName)
        private val tvLength: TextView = itemView.findViewById(R.id.tvLength)
        private val tvWidth: TextView = itemView.findViewById(R.id.tvWidth)
        private val tvHeight: TextView = itemView.findViewById(R.id.tvHeight)
        private val tvVolume: TextView = itemView.findViewById(R.id.tvVolume)
        private val tvDate: TextView = itemView.findViewById(R.id.tvDate)
        private val btnDelete: MaterialButton = itemView.findViewById(R.id.btnDelete)

        fun bind(measurement: MeasurementResult) {
            val dateFormat = SimpleDateFormat("dd MMM yyyy, HH:mm", Locale.getDefault())

            // Set package name
            tvPackageName.text = measurement.packageName ?: "Package Measurement"

            // Convert from meters to centimeters and set individual dimensions
            val lengthCm = String.format("%.1f", measurement.width * 100)
            val widthCm = String.format("%.1f", measurement.height * 100)
            val heightCm = String.format("%.1f", measurement.depth * 100)

            tvLength.text = lengthCm
            tvWidth.text = widthCm
            tvHeight.text = heightCm

            // Convert from cubic meters to cubic centimeters and format volume
            val volumeCm3 = measurement.volume * 1_000_000
            tvVolume.text = "Volume: ${String.format("%.0f", volumeCm3)} cm³"

            // Set formatted date
            tvDate.text = dateFormat.format(Date(measurement.timestamp))

            // Load thumbnail image using Glide
            loadThumbnailImage(measurement)

            // Set click listeners
            itemView.setOnClickListener {
                onItemClick(measurement)
            }

            btnDelete.setOnClickListener {
                onDeleteClick(measurement)
            }
        }

        private fun loadThumbnailImage(measurement: MeasurementResult) {
            // Since MeasurementResult doesn't have imagePath, use a placeholder
            // You can customize this based on package size or other criteria
            val placeholderIcon = when {
                measurement.volume * 1_000_000 <= 1000 -> R.drawable.ic_local_shipping // Small package
                measurement.volume * 1_000_000 <= 5000 -> R.drawable.ic_local_shipping // Medium package
                else -> R.drawable.ic_local_shipping // Large package
            }

            Glide.with(itemView.context)
                .load(placeholderIcon)
                .apply(
                    com.bumptech.glide.request.RequestOptions()
                        .transform(CenterCrop(), RoundedCorners(16))
                )
                .into(ivPackageThumbnail)
        }
    }

    private class MeasurementDiffCallback : DiffUtil.ItemCallback<MeasurementResult>() {
        override fun areItemsTheSame(oldItem: MeasurementResult, newItem: MeasurementResult): Boolean {
            return oldItem.id == newItem.id
        }

        override fun areContentsTheSame(oldItem: MeasurementResult, newItem: MeasurementResult): Boolean {
            return oldItem == newItem
        }
    }
}

package com.paxel.arspacescan.ui.history

import android.view.LayoutInflater
import android.view.View
import android.view.ViewGroup
import android.widget.ImageView
import android.widget.LinearLayout
import android.widget.TextView
import androidx.recyclerview.widget.DiffUtil
import androidx.recyclerview.widget.ListAdapter
import androidx.recyclerview.widget.RecyclerView
import com.bumptech.glide.Glide
import com.bumptech.glide.load.resource.bitmap.CenterCrop
import com.bumptech.glide.load.resource.bitmap.RoundedCorners
import com.google.android.material.button.MaterialButton
import com.paxel.arspacescan.R
import com.paxel.arspacescan.data.model.MeasurementResult
import java.text.SimpleDateFormat
import java.util.Date
import java.util.Locale

class MeasurementAdapter(
    private val onItemClick: (MeasurementResult) -> Unit,
    private val onDeleteClick: (MeasurementResult) -> Unit
) : ListAdapter<MeasurementResult, MeasurementAdapter.ViewHolder>(MeasurementDiffCallback()) {

    override fun onCreateViewHolder(parent: ViewGroup, viewType: Int): ViewHolder {
        val view = LayoutInflater.from(parent.context)
            .inflate(R.layout.item_measurement, parent, false)
        return ViewHolder(view)
    }

    override fun onBindViewHolder(holder: ViewHolder, position: Int) {
        holder.bind(getItem(position))
    }

    inner class ViewHolder(itemView: View) : RecyclerView.ViewHolder(itemView) {
        private val ivPackageThumbnail: ImageView = itemView.findViewById(R.id.ivPackageThumbnail)
        private val tvPackageName: TextView = itemView.findViewById(R.id.tvPackageName)
        private val tvDate: TextView = itemView.findViewById(R.id.tvDate)
        private val btnExpand: MaterialButton = itemView.findViewById(R.id.btnExpand)
        private val layoutExpandableDetails: LinearLayout = itemView.findViewById(R.id.layoutExpandableDetails)
        private val tvLength: TextView = itemView.findViewById(R.id.tvLength)
        private val tvWidth: TextView = itemView.findViewById(R.id.tvWidth)
        private val tvHeight: TextView = itemView.findViewById(R.id.tvHeight)
        private val tvVolume: TextView = itemView.findViewById(R.id.tvVolume)
        private val btnDelete: MaterialButton = itemView.findViewById(R.id.btnDelete)

        private var isExpanded = false

        fun bind(measurement: MeasurementResult) {
            val dateFormat = SimpleDateFormat("dd MMM yyyy, HH:mm", Locale.getDefault())

            // Set package name
            tvPackageName.text = measurement.packageName ?: "Package Measurement"

            // Set formatted date
            tvDate.text = dateFormat.format(Date(measurement.timestamp))

            // Convert from meters to centimeters and set individual dimensions
            val lengthCm = String.format("%.1f", measurement.width * 100)
            val widthCm = String.format("%.1f", measurement.height * 100)
            val heightCm = String.format("%.1f", measurement.depth * 100)

            tvLength.text = lengthCm
            tvWidth.text = widthCm
            tvHeight.text = heightCm

            // Convert from cubic meters to cubic centimeters and format volume
            val volumeCm3 = measurement.volume * 1_000_000
            tvVolume.text = "Volume: ${String.format("%.0f", volumeCm3)} cm³"

            // Load thumbnail image using Glide
            loadThumbnailImage(measurement)

            // Setup expand/collapse functionality
            setupExpandCollapse()

            // Set click listeners
            itemView.setOnClickListener {
                onItemClick(measurement)
                toggleExpanded()
            }

            btnExpand.setOnClickListener {
                toggleExpanded()
            }

            btnDelete.setOnClickListener {
                onDeleteClick(measurement)
            }
        }

        private fun setupExpandCollapse() {
            updateExpandIcon()
            layoutExpandableDetails.visibility = if (isExpanded) View.VISIBLE else View.GONE
        }

        private fun toggleExpanded() {
            isExpanded = !isExpanded
            updateExpandIcon()

            if (isExpanded) {
                layoutExpandableDetails.visibility = View.VISIBLE
            } else {
                layoutExpandableDetails.visibility = View.GONE
            }
        }

        private fun updateExpandIcon() {
            val iconRes = if (isExpanded) {
                R.drawable.ic_expand_less
            } else {
                R.drawable.ic_expand_more
            }
            btnExpand.setIconResource(iconRes)
        }

        private fun loadThumbnailImage(measurement: MeasurementResult) {
            // Since MeasurementResult doesn't have imagePath, use a placeholder
            // You can customize this based on package size or other criteria
            val placeholderIcon = when {
                measurement.volume * 1_000_000 <= 1000 -> R.drawable.ic_local_shipping // Small package
                measurement.volume * 1_000_000 <= 5000 -> R.drawable.ic_local_shipping // Medium package  
                else -> R.drawable.ic_local_shipping // Large package
            }

            Glide.with(itemView.context)
                .load(placeholderIcon)
                .apply(
                    com.bumptech.glide.request.RequestOptions()
                        .transform(CenterCrop(), RoundedCorners(16))
                )
                .into(ivPackageThumbnail)
        }
    }

    private class MeasurementDiffCallback : DiffUtil.ItemCallback<MeasurementResult>() {
        override fun areItemsTheSame(oldItem: MeasurementResult, newItem: MeasurementResult): Boolean {
            return oldItem.id == newItem.id
        }

        override fun areContentsTheSame(oldItem: MeasurementResult, newItem: MeasurementResult): Boolean {
            return oldItem == newItem
        }
    }
}

package com.paxel.arspacescan.ui.main

import android.Manifest
import android.content.Intent
import android.content.pm.PackageManager
import android.os.Bundle
import android.os.Handler
import android.os.Looper
import android.widget.Toast
import androidx.appcompat.app.AppCompatActivity
import androidx.core.app.ActivityCompat
import androidx.core.content.ContextCompat
import com.google.android.material.button.MaterialButton
import com.google.ar.core.ArCoreApk
import com.paxel.arspacescan.R
import com.paxel.arspacescan.ui.common.safeHapticFeedback
import com.paxel.arspacescan.ui.history.HistoryActivity
import com.paxel.arspacescan.ui.measurement.ARMeasurementActivity

class MainActivity : AppCompatActivity() {

    private val CAMERA_PERMISSION_CODE = 100
    private var isSplashScreen = true

    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)

        // Tampilkan splash screen
        setContentView(R.layout.activity_splash)

        Handler(Looper.getMainLooper()).postDelayed({
            isSplashScreen = false
            setContentView(R.layout.activity_main)
            setupMainMenu()
        }, 2000)
    }

    private fun setupMainMenu() {
        val startMeasurementButton = findViewById<MaterialButton>(R.id.btnStartMeasurement)
        val historyButton = findViewById<MaterialButton>(R.id.btnHistory)

        startMeasurementButton.setOnClickListener {
            it.safeHapticFeedback()
            checkPermissionsAndStartMeasurement()
        }

        historyButton.setOnClickListener {
            it.safeHapticFeedback()
            startActivity(Intent(this, HistoryActivity::class.java))
        }
    }

    private fun checkPermissionsAndStartMeasurement() {
        // Cek ketersediaan ARCore
        val availability = ArCoreApk.getInstance().checkAvailability(this)
        if (availability.isTransient) {
            Handler(Looper.getMainLooper()).postDelayed({
                checkPermissionsAndStartMeasurement()
            }, 200)
            return
        }

        if (availability.isSupported) {
            if (ContextCompat.checkSelfPermission(this, Manifest.permission.CAMERA)
                != PackageManager.PERMISSION_GRANTED
            ) {
                ActivityCompat.requestPermissions(
                    this,
                    arrayOf(Manifest.permission.CAMERA),
                    CAMERA_PERMISSION_CODE
                )
            } else {
                showPackageInputDialog()
            }
        } else {
            Toast.makeText(
                this,
                "Perangkat ini tidak mendukung ARCore",
                Toast.LENGTH_LONG
            ).show()
        }
    }

    private fun showPackageInputDialog() {
        val dialog = PackageInputDialog()
        dialog.setOnPackageNameEnteredListener { packageName, declaredSize ->
            val intent = Intent(this, ARMeasurementActivity::class.java)
            intent.putExtra("PACKAGE_NAME", packageName)
            intent.putExtra("DECLARED_SIZE", declaredSize)
            startActivity(intent)
        }
        dialog.show(supportFragmentManager, "PackageInputDialog")
    }

    override fun onRequestPermissionsResult(
        requestCode: Int,
        permissions: Array<out String>,
        grantResults: IntArray
    ) {
        super.onRequestPermissionsResult(requestCode, permissions, grantResults)
        if (requestCode == CAMERA_PERMISSION_CODE) {
            if (grantResults.isNotEmpty() && grantResults[0] == PackageManager.PERMISSION_GRANTED) {
                showPackageInputDialog()
            } else {
                Toast.makeText(
                    this,
                    "Izin kamera diperlukan untuk menggunakan fitur AR",
                    Toast.LENGTH_LONG
                ).show()
            }
        }
    }
}

package com.paxel.arspacescan.ui.main

import android.Manifest
import android.content.Intent
import android.content.pm.PackageManager
import android.os.Bundle
import android.os.Handler
import android.os.Looper
import android.widget.Toast
import androidx.appcompat.app.AppCompatActivity
import androidx.core.app.ActivityCompat
import androidx.core.content.ContextCompat
import com.google.android.material.button.MaterialButton
import com.google.ar.core.ArCoreApk
import com.paxel.arspacescan.R
import com.paxel.arspacescan.ui.common.safeHapticFeedback
import com.paxel.arspacescan.ui.history.HistoryActivity
import com.paxel.arspacescan.ui.measurement.ARMeasurementActivity

class MainActivity : AppCompatActivity(), PackageInputDialog.OnPackageInputListener {

    private val CAMERA_PERMISSION_CODE = 100
    private var isSplashScreen = true

    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)

        // Tampilkan splash screen
        setContentView(R.layout.activity_splash)

        Handler(Looper.getMainLooper()).postDelayed({
            isSplashScreen = false
            setContentView(R.layout.activity_main)
            setupMainMenu()
        }, 2000)
    }

    private fun setupMainMenu() {
        val startMeasurementButton = findViewById<MaterialButton>(R.id.btnStartMeasurement)
        val historyButton = findViewById<MaterialButton>(R.id.btnHistory)

        startMeasurementButton.setOnClickListener {
            it.safeHapticFeedback()
            checkPermissionsAndStartMeasurement()
        }

        historyButton.setOnClickListener {
            it.safeHapticFeedback()
            startActivity(Intent(this, HistoryActivity::class.java))
        }
    }

    private fun checkPermissionsAndStartMeasurement() {
        // Cek ketersediaan ARCore
        val availability = ArCoreApk.getInstance().checkAvailability(this)
        if (availability.isTransient) {
            Handler(Looper.getMainLooper()).postDelayed({
                checkPermissionsAndStartMeasurement()
            }, 200)
            return
        }

        if (availability.isSupported) {
            if (ContextCompat.checkSelfPermission(this, Manifest.permission.CAMERA)
                != PackageManager.PERMISSION_GRANTED
            ) {
                ActivityCompat.requestPermissions(
                    this,
                    arrayOf(Manifest.permission.CAMERA),
                    CAMERA_PERMISSION_CODE
                )
            } else {
                showPackageInputDialog()
            }
        } else {
            Toast.makeText(
                this,
                "Perangkat ini tidak mendukung ARCore",
                Toast.LENGTH_LONG
            ).show()
        }
    }

    private fun showPackageInputDialog() {
        val dialog = PackageInputDialog()
        dialog.show(supportFragmentManager, "PackageInputDialog")
    }

    override fun onRequestPermissionsResult(
        requestCode: Int,
        permissions: Array<out String>,
        grantResults: IntArray
    ) {
        super.onRequestPermissionsResult(requestCode, permissions, grantResults)
        if (requestCode == CAMERA_PERMISSION_CODE) {
            if (grantResults.isNotEmpty() && grantResults[0] == PackageManager.PERMISSION_GRANTED) {
                showPackageInputDialog()
            } else {
                Toast.makeText(
                    this,
                    "Izin kamera diperlukan untuk menggunakan fitur AR",
                    Toast.LENGTH_LONG
                ).show()
            }
        }
    }

    // Implementation of OnPackageInputListener interface
    override fun onPackageInput(packageName: String, declaredSize: String) {
        // Handle package input data received from PackageInputDialog
        // Start ARMeasurementActivity with the package information
        val intent = Intent(this, ARMeasurementActivity::class.java)
        intent.putExtra("PACKAGE_NAME", packageName)
        intent.putExtra("DECLARED_SIZE", declaredSize)
        startActivity(intent)
    }
}

package com.paxel.arspacescan.ui.main

import android.app.Dialog
import android.content.Context
import android.os.Bundle
import android.view.LayoutInflater
import android.widget.ArrayAdapter
import android.widget.AutoCompleteTextView
import android.widget.Button
import android.widget.EditText
import androidx.appcompat.app.AlertDialog
import androidx.fragment.app.DialogFragment
import com.google.android.material.textfield.TextInputLayout
import com.paxel.arspacescan.R

class PackageInputDialog : DialogFragment() {

    interface OnPackageInputListener {
        fun onPackageInput(packageName: String, declaredSize: String)
    }

    private var listener: OnPackageInputListener? = null

    fun setOnPackageNameEnteredListener(callback: (String, String) -> Unit) {
        listener = object : OnPackageInputListener {
            override fun onPackageInput(packageName: String, declaredSize: String) {
                callback(packageName, declaredSize)
            }
        }
    }

    override fun onAttach(context: Context) {
        super.onAttach(context)
        try {
            listener = context as OnPackageInputListener
        } catch (e: ClassCastException) {
            throw ClassCastException("$context must implement OnPackageInputListener")
        }
    }

    override fun onCreateDialog(savedInstanceState: Bundle?): Dialog {
        val inflater = LayoutInflater.from(requireContext())
        val view = inflater.inflate(R.layout.dialog_package_input, null)

        val etPackageName = view.findViewById<EditText>(R.id.etPackageName)
        val tilDeclaredSize = view.findViewById<TextInputLayout>(R.id.tilDeclaredSize)
        val actvDeclaredSize = view.findViewById<AutoCompleteTextView>(R.id.actvDeclaredSize)

        // Set up the declared size dropdown with predefined options
        val sizeOptions = resources.getStringArray(R.array.package_size_options)
        val sizeAdapter = ArrayAdapter(requireContext(), android.R.layout.simple_dropdown_item_1line, sizeOptions)
        actvDeclaredSize.setAdapter(sizeAdapter)

        // Set default package name
        etPackageName.setText(getString(R.string.default_package_name))

        return AlertDialog.Builder(requireContext())
            .setTitle(R.string.package_name_title)
            .setView(view)
            .setPositiveButton(R.string.ok) { _, _ ->
                val packageName = etPackageName.text.toString().trim()
                val declaredSize = actvDeclaredSize.text.toString().trim()

                val finalPackageName = if (packageName.isEmpty()) {
                    getString(R.string.default_package_name)
                } else {
                    packageName
                }

                val finalDeclaredSize = if (declaredSize.isEmpty()) {
                    getString(R.string.select_declared_size_prompt)
                } else {
                    declaredSize
                }

                listener?.onPackageInput(finalPackageName, finalDeclaredSize)
            }
            .setNegativeButton(R.string.cancel) { dialog, _ ->
                dialog.dismiss()
            }
            .create()
    }

    companion object {
        const val TAG = "PackageInputDialog"

        fun newInstance(): PackageInputDialog {
            return PackageInputDialog()
        }
    }
}

